try {
    const selectedComponent = context.component?.variables?.['selected_component_var'];
    const quantity = context.component?.variables?.['quantity_var'] || 1;
    const componentToEdit = context.component?.props?.['component_to_edit_arg'];

    if (!selectedComponent) {
      return;
    }

    const currentBOM = [...(variables['328a9e02-9717-403e-b56e-86178e366ccd'] || [])];

    const bomComponent = {
      id: selectedComponent.id,
      sku: selectedComponent.sku,
      name: selectedComponent.name,
      quantity: quantity,
      uom: selectedComponent.uom || 'EA',
      description: selectedComponent.description || '',
      defaultLocation: selectedComponent.defaultLocation || '',
      onHand: selectedComponent.onHand || 0
    };

    if (componentToEdit) {
      // Edit existing component
      const index = currentBOM.findIndex(comp => comp.id === componentToEdit.id);
      if (index > -1) {
        currentBOM[index] = bomComponent;
      }
    } else {
      // Add new component (check for duplicates)
      const existingIndex = currentBOM.findIndex(comp => comp.id === selectedComponent.id);
      if (existingIndex > -1) {
        // Update quantity if component already exists
        currentBOM[existingIndex].quantity += quantity;
      } else {
        // Add new component
        currentBOM.push(bomComponent);
      }
    }

    // Update BOM and check for changes
    variables['328a9e02-9717-403e-b56e-86178e366ccd'] = currentBOM;
    await utilsFunctions.executeGlobalFunction['3b5a8df0-ff47-4de4-96a3-4601861216f3']();

    // Reset popup state
    variables['3ab8963d-28cd-4742-91d7-fe8f1d945171'] = false;
    variables['c6a4e071-56ae-417e-a483-f3976b69d217'] = '';

    // Close popup
    utilsFunctions.closePopup(context, bomComponent);

    const action = componentToEdit ? 'updated' : 'added';
    await utilsFunctions.executeGlobalFunction['9939d68d-c8ea-476b-b1a2-d90613b686e6'](`Component ${action} successfully`, 'success');

  } catch (error) {
    await utilsFunctions.executeGlobalFunction['9939d68d-c8ea-476b-b1a2-d90613b686e6']('Failed to add component', 'error');
  }
