  const statusFilter = variables['2af5288e-1bd2-4229-8714-ab7fe3156c15'] || 'all';
  const locationFilter = variables['e6cf9fc1-2823-499b-9802-02d501592df6'] || 'all';

  return sessions.filter(session => {
    // Search filter
    const matchesSearch = !searchQuery || 
      session.name.toLowerCase().includes(searchQuery) ||
      session.location.toLowerCase().includes(searchQuery);

    // Status filter
    const matchesStatus = statusFilter === 'all' || session.status === statusFilter;

    // Location filter
    const matchesLocation = locationFilter === 'all' || session.location === locationFilter;

    return matchesSearch && matchesStatus && matchesLocation;
  });

