// Create new stocktake session
const newSession = {
  id: `st-${Date.now()}`,
  name: stocktakeName,
  location: location?.name || 'Unknown Location',
  status: 'in_progress',
  startedAt: new Date().toISOString(),
  completedAt: null,
  createdBy: variables['16c3d9ec-dd7a-48bb-9e20-4c5887f28770']?.name || 'Current User',
  componentCount: 0,
  includeAllComponents: includeAllComponents,
  componentFilter: componentFilter
};

return newSession;

