const data = context.workflow['action_1'].result;
const currentComponent = variables['22d286e6-0449-480d-954f-24cc1fab2a4e'];
const currentOnHand = currentComponent?.onHand || 0;
const adjustmentAmount = data.adjustmentType === 'add' ? data.quantity : -data.quantity;
const newOnHand = Math.max(0, currentOnHand + adjustmentAmount);

return {
  currentComponent,
  currentOnHand,
  adjustmentAmount,
  newOnHand,
  data
};

