const components = variables['aba0ff58-5bf6-44fe-bb4d-ee2024adc146']?.componentUsage?.components || [];
  const dateRange = filters.dateRange || 'last30';
  const selectedComponents = filters.components || [];

  let filteredComponents = components;

  // Filter by selected components if any
  if (selectedComponents.length > 0) {
    filteredComponents = components.filter(component => 
      selectedComponents.includes(component.id)
    );
  }

  // Sort by usage based on date range
  switch (dateRange) {
    case 'last30':
      filteredComponents = filteredComponents.sort((a, b) => b.usedLast30Days - a.usedLast30Days);
      break;
    case 'last90':
      filteredComponents = filteredComponents.sort((a, b) => b.usedLast90Days - a.usedLast90Days);
      break;
    case 'lastYear':
    case 'all':
    default:
      filteredComponents = filteredComponents.sort((a, b) => b.totalUsed - a.totalUsed);
      break;
  }

  return filteredComponents;

const variances = variables['aba0ff58-5bf6-44fe-bb4d-ee2024adc146']?.stocktakeVariances?.variances || [];
  const dateRange = filters.dateRange || 'all';
  const location = filters.location || 'all';
  const varianceType = filters.varianceType || 'all';

  return variances.filter(variance => {
    // Location filter
    const matchesLocation = location === 'all' || variance.location === location;

    // Variance type filter
    let matchesVarianceType = true;
    if (varianceType !== 'all') {
      switch (varianceType) {
        case 'positive':
          matchesVarianceType = variance.variance > 0;
          break;
        case 'negative':
          matchesVarianceType = variance.variance < 0;
          break;
      }
    }

    // Date range filter
    let matchesDateRange = true;
    if (dateRange !== 'all') {
      const varianceDate = new Date(variance.date);
      const now = new Date();
      const daysAgo = dateRange === 'last30' ? 30 : dateRange === 'last90' ? 90 : 365;
      const cutoffDate = new Date(now.getTime() - (daysAgo * 24 * 60 * 60 * 1000));
      matchesDateRange = varianceDate >= cutoffDate;
    }

    return matchesLocation && matchesVarianceType && matchesDateRange;
  });

