    id: Math.random().toString(36).substr(2, 9)
  };

  variables['83057ba1-2deb-4823-9f12-7ecef9d35a5d'] = toastData;

  // Auto-hide toast after 4 seconds
  setTimeout(() => {
    if (variables['83057ba1-2deb-4823-9f12-7ecef9d35a5d']?.id === toastData.id) {
      variables['83057ba1-2deb-4823-9f12-7ecef9d35a5d'] = null;
