    line.variance !== 0 && line.countedQty !== null && line.countedQty !== undefined
  ).length;

  const totalVariance = lines.reduce((sum, line) => {
    return sum + (line.variance || 0);
  }, 0);

  // Count items with status "ADJUSTED" (case-insensitive)
  const adjustedItems = lines.filter(line => 
    line.status && line.status.toLowerCase() === 'adjusted'
  ).length;

  return {
    totalItems,
    countedItems,
    pendingItems,
    matchingItems,
    varianceItems,
    totalVariance,
    adjustedItems
  };
