const components = variables['e6cede44-4607-473b-b4a2-d4020bac2f2b'] || [];
  const searchQuery = (variables['9d1f3443-131d-41bf-a281-f281df1e698a'] || '').toLowerCase();
  const locationFilter = variables['c3b69a58-1421-414e-a361-304760bdfe57'] || 'all';
  const stockFilter = variables['ab17dcec-9261-4957-986a-c7392305aed3'] || 'all';
  const statusFilter = variables['68eb1df5-599b-467e-86a5-aad1e7dab9c8'] || 'all';

  return components.filter(component => {
    // Search filter
    const matchesSearch = !searchQuery || 
      component.name.toLowerCase().includes(searchQuery) ||
      component.sku.toLowerCase().includes(searchQuery);

    // Location filter
    const matchesLocation = locationFilter === 'all' || component.defaultLocation === locationFilter;

    // Stock filter
    const matchesStock = stockFilter === 'all' ||
      (stockFilter === 'low' && component.onHand <= component.reorderPoint) ||
      (stockFilter === 'in_stock' && component.onHand > component.reorderPoint);

    // Status filter
    const matchesStatus = statusFilter === 'all' || component.status === statusFilter;

    return matchesSearch && matchesLocation && matchesStock && matchesStatus;
  });

